import "./App.css";
import { useState } from "react";

import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Stack from "react-bootstrap/Stack";
import Form from "react-bootstrap/Form";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Codes from "./Codes";

function App() {
  const [lang1, setLang1] = useState("");
  const [lang2, setLang2] = useState("");
  const [prog1, setProg1] = useState("");
  const [prog2, setProg2] = useState("");
  const [program1, setProgram1] = useState("");
  const [program2, setProgram2] = useState("");

  const findMatch = () => {};

  const handleLang1 = (e) => {
    setLang1(e.target.value);
  };

  const handleLang2 = (e) => {
    setLang2(e.target.value);
  };

  const handleProgram = (lang, prog) =>{
    let programName = "";
    switch (prog) {
      case "mergesort":
        programName = lang + "MergeSort";
        break;
        case "insertionsort":
        programName = lang + "MergeSort";
        break;
        case "mergesort":
        programName = lang + "MergeSort";
        break;
        case "mergesort":
        programName = lang + "MergeSort";
        break;
        case "mergesort":
        programName = lang + "MergeSort";
        break;
    }
    switch (lang) {
      case "Java":
        setProgram1(codes.javaMergeSort);
        break;
      case "C++":
        setProgram1(codes.cppMergeSort);
        break;
      case "Python":
        setProgram1(codes.pythonMergeSort);
        break;
      default:
        setProgram1("");
        break;
    }
  }

  const handleProg1 = (e) => {

  };

  const handleProg2 = (e) => {
    console.log(e.target.value);

    let prog = "";

    const codes = new Codes();

    switch (e.target.value) {
      case "1":
        prog = "merge-sort";
        setProgram2(codes.mergeSort);
        break;
      case "2":
        prog = "selection-sort";
        setProgram2(codes.selectionSort);
        break;
      case "3":
        prog = "quick-sort";
        setProgram2(codes.quickSort);
        break;
      case "4":
        prog = "insertion-sort";
        setProgram2(codes.insertionSort);
        break;
      case "5":
        prog = "bubble-sort";
        setProgram2(codes.bubbleSort);
        break;
      default:
        prog = "";
        break;
    }

    setProg2(prog);
    console.log(prog);
  };

  return (
    <div className="App">
      <Container fluid>
        <Row>
          <Col md={12} style={{ height: "10vh" }}>
            <Card className="headerCard">
              <Card.Body>
                <div className="bg-blue-800">
                  <h1>Prototype for Type 4 Clone Detection using TestCases</h1>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col md={2} style={{ height: "100vh" }}>
            <Card className="leftCard">
              <Card.Body>
                <Stack gap={5}>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleLang1}
                  >
                    <option>Language 1</option>
                    <option value="java"> Java</option>
                    <option value="cpp">C++</option>
                    <option value="python">Python</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleLang2}
                  >
                    <option>Language 2</option>
                    <option value="java">Java</option>
                    <option value="cpp">C++</option>
                    <option value="python">Python</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleProg1}
                  >
                    <option>Program 1</option>
                    <option value="mergeSort">mergeSort</option>
                    <option value="selectionSort">selectionSort</option>
                    <option value="quickSort">quickSort</option>
                    <option value="insertionSort">insertionSort</option>
                    <option value="bubbleSort">bubbleSort</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleProg2}
                  >
                    <option>Program 2</option>
                    <option value="mergeSort">mergeSort</option>
                    <option value="selectionSort">selectionSort</option>
                    <option value="quickSort">quickSort</option>
                    <option value="insertionSort">insertionSort</option>
                    <option value="bubbleSort">bubbleSort</option>
                  </Form.Select>
                  <Button variant="dark" className="button" onClick={findMatch}>
                    Match
                  </Button>
                </Stack>
              </Card.Body>
            </Card>
          </Col>
          <Col md={10}>
            <Row md={9}>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>{program1}</Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>{program2}</Card.Body>
                </Card>
              </Col>
            </Row>
            <Row md={3}>
              <Col md={12}>
                <Card className="bottomCard">
                  <Card.Body>{/* <h1>Hellow World</h1> */}</Card.Body>
                </Card>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
