import "./App.css";
import { useState } from "react";

import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Stack from "react-bootstrap/Stack";
import Form from "react-bootstrap/Form";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

function App() {

  const [lang1, setLang1] = useState("");
  const [lang2, setLang2] = useState("");
  const [prog1, setProg1] = useState("");
  const [prog2, setProg2] = useState("");

  const findMatch = () => {

  }

  const handleLang1 = (e) => {
    console.log(e.target.value);
    setLang1(e.target.value);
  }

  const handleLang2 = (e) => {
    console.log(e.target.value);

  }

  const handleProg1 = (e) => {
    console.log(e.target.value);
  }

  const handleProg2 = (e) => {
    console.log(e.target.value);
  }

  return (
    <div className="App">
      <Container fluid>
        <Row>
          <Col md={12} style={{ height: "10vh" }}>
            <Card className="headerCard">
              <Card.Body>
                <div className="bg-blue-800">
                  <h1>Prototype for Type 4 Clone Detection using TestCases</h1>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col md={2} style={{ height: "100vh" }}>
            <Card className="leftCard">
              <Card.Body>
                <Stack gap={5}>
                  <Form.Select aria-label="Default select example" className="item" onChange={handleLang1}>
                    <option>Category</option>
                    <option value="1"> Programs</option>
                    <option value="2">Searching Programs</option>
                    <option value="3">Something else</option>
                  </Form.Select>
                  <Form.Select aria-label="Default select example" className="item" onChange={handleLang2}>
                    <option>Language</option>
                    <option value="1">Python</option>
                    <option value="2">CPP</option>
                    <option value="3">Java</option>
                  </Form.Select>
                  <Form.Select aria-label="Default select example" className="item" onChange={handleProg1}>
                    <option>Program 1</option>
                    <option value="1">merge-sort</option>
                    <option value="2">selection-sort</option>
                    <option value="3">quick-sort</option>
                  </Form.Select>
                  <Form.Select aria-label="Default select example" className="item" onChange={handleProg2}>
                    <option>Program 2</option>
                    <option value="1">merge-sort</option>
                    <option value="2">selection-sort</option>
                    <option value="3">quick-sort</option>
                  </Form.Select>
                  <Button variant="dark" className="button" onClick={findMatch}>Match</Button>
                </Stack>
              </Card.Body>
            </Card>
          </Col>
          <Col md={10}>
            <Row md={9}>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>
                    <h1>Hello World</h1>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>
                    {/* <h1>Hello World</h1> */}
                  </Card.Body>
                </Card>
              </Col>
            </Row>
            <Row md={3}>
              <Col md={12}>
                <Card className="bottomCard">
                  <Card.Body>
                    {/* <h1>Hellow World</h1> */}
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
