import "./App.css";
import { useState } from "react";

import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Stack from "react-bootstrap/Stack";
import Form from "react-bootstrap/Form";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Codes from "./Codes";
import AceEditor from "react-ace";

function App() {
  const [lang1, setLang1] = useState("");
  const [lang2, setLang2] = useState("");
  const [prog1, setProg1] = useState("");
  const [prog2, setProg2] = useState("");
  const [program1, setProgram1] = useState("");
  const [program2, setProgram2] = useState("");

  const findMatch = () => {};

  const handleLang1 = (e) => {
    setLang1(e.target.value);
  };

  const handleLang2 = (e) => {
    setLang2(e.target.value);
  };

  const handleProgram = (programName, progNum) => {
    let codes = new Codes();
    switch (programName) {
      case "cppMergeSort":
        progNum === 1 ? setProgram1(codes.cppMergeSort) : setProgram2(codes.cppMergeSort);
        break;
      case "cppQuickSort":
        progNum === 1 ? setProgram1(codes.cppQuickSort) : setProgram2(codes.cppQuickSort);
        break;
      case "cppSelectionSort":
        progNum === 1 ? setProgram1(codes.cppSelectionSort) : setProgram2(codes.cppSelectionSort);
        break;
      case "cppBubbleSort":
        progNum === 1 ? setProgram1(codes.cppBubbleSort) : setProgram2(codes.cppBubbleSort);
        break;
      case "cppInsertionSort":
        progNum === 1 ? setProgram1(codes.cppInsertionSort) : setProgram2(codes.cppInsertionSort);
        break;
      case "javaBubbleSort":
        progNum === 1 ? setProgram1(codes.javaBubbleSort) : setProgram2(codes.javaBubbleSort);
        break;
      case "javaInsertionSort":
        progNum === 1 ? setProgram1(codes.javaInsertionSort) : setProgram2(codes.javaInsertionSort);       
        break;
      case "javaMergeSort":
        progNum === 1 ? setProgram1(codes.javaMergeSort) : setProgram2(codes.javaMergeSort);
        break;
      case "javaQuickSort":
        progNum === 1 ? setProgram1(codes.javaQuickSort) : setProgram2(codes.javaQuickSort);
        break;
      case "javaSelectionSort":
        progNum === 1 ? setProgram1(codes.javaSelectionSort) : setProgram2(codes.javaSelectionSort);
        break;
      case "pythonBubbleSort":
        progNum === 1 ? setProgram1(codes.pythonBubbleSort) : setProgram2(codes.pythonBubbleSort);
        break;
      case "pythonInsertionSort":
        progNum === 1 ? setProgram1(codes.pythonInsertionSort) : setProgram2(codes.pythonInsertionSort);
        break;
      case "pythonMergeSort":
        progNum === 1 ? setProgram1(codes.pythonMergeSort) : setProgram2(codes.pythonMergeSort);
        break;
      case "pythonQuickSort":
        progNum === 1 ? setProgram1(codes.pythonQuickSort) : setProgram2(codes.pythonQuickSort);
        break;
      case "pythonSelectionSort":
        progNum === 1 ? setProgram1(codes.pythonSelectionSort) : setProgram2(codes.pythonSelectionSort);
        break;
      default:
        break;
    }
  };

  const handleProg1 = (e) => {
    setProg1(e.target.value);
    let programName = lang1 + e.target.value;
    handleProgram(programName, 1);
  };

  const handleProg2 = (e) => {
    setProg2(e.target.value);
    let programName = lang2 + e.target.value;
    handleProgram(programName, 2);
  };

  return (
    <div className="App">
      <Container fluid>
        <Row>
          <Col md={12} style={{ height: "10vh" }}>
            <Card className="headerCard">
              <Card.Body>
                <div className="bg-blue-800">
                  <h1>Prototype for Type 4 Clone Detection using TestCases</h1>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col md={2} style={{ height: "100vh" }}>
            <Card className="leftCard">
              <Card.Body>
                <Stack gap={5}>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleLang1}
                  >
                    <option>Language 1</option>
                    <option value="java"> Java</option>
                    <option value="cpp">C++</option>
                    <option value="python">Python</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleLang2}
                  >
                    <option>Language 2</option>
                    <option value="java">Java</option>
                    <option value="cpp">C++</option>
                    <option value="python">Python</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleProg1}
                  >
                    <option>Program 1</option>
                    <option value="MergeSort">Merge Sort</option>
                    <option value="SelectionSort">Selection Sort</option>
                    <option value="QuickSort">Quick Sort</option>
                    <option value="InsertionSort">Insertion Sort</option>
                    <option value="BubbleSort">Bubble Sort</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleProg2}
                  >
                    <option>Program 2</option>
                    <option value="MergeSort">Merge Sort</option>
                    <option value="SelectionSort">Selection Sort</option>
                    <option value="QuickSort">Quick Sort</option>
                    <option value="InsertionSort">Insertion Sort</option>
                    <option value="BubbleSort">Bubble Sort</option>
                  </Form.Select>
                  <Button variant="dark" className="button" onClick={findMatch}>
                    Match
                  </Button>
                </Stack>
              </Card.Body>
            </Card>
          </Col>
          <Col md={10}>
            <Row md={9}>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>{program1}</Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>
                    <AceEditor>

                    </AceEditor>

                  </Card.Body>
                </Card>
              </Col>
            </Row>
            <Row md={3}>
              <Col md={12}>
                <Card className="bottomCard">
                  <Card.Body>{/* <h1>Hellow World</h1> */}</Card.Body>
                </Card>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
