import "./App.css";
import { useState } from "react";

import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Stack from "react-bootstrap/Stack";
import Form from "react-bootstrap/Form";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Codes from "./Codes";

function App() {
  const [lang1, setLang1] = useState("");
  const [lang2, setLang2] = useState("");
  const [prog1, setProg1] = useState("");
  const [prog2, setProg2] = useState("");
  const [program1, setProgram1] = useState("");
  const [program2, setProgram2] = useState("");

  const findMatch = () => {};

  const handleLang1 = (e) => {
    setLang1(e.target.value);
  };

  const handleLang2 = (e) => {
    setLang2(e.target.value);
  };

  const handleProgram = (programName, setProg) => {
    switch (programName) {
      case "cppMergeSort":
        setProg(Codes.cppMergeSort);
        break;
      case "cppQuickSort":
        setProg(Codes.cppQuickSort);
        break;
      case "cppSelectionSort":
        setProg(Codes.cppSelectionSort);
        break;
      case "cppBubbleSort":
        setProg(Codes.cppBubbleSort);
        break;
      case "cppInsertionSort":
        setProg(Codes.cppInsertionSort);
        break;
      case "javaBubbleSort":
        setProg(Codes.javaBubbleSort);
        break;
      case "javaInsertionSort":
        setProg(Codes.javaInsertionSort);
        break;
      case "javaMergeSort":
        setProg(Codes.javaMergeSort);
        break;
      case "javaQuickSort":
        setProg(Codes.javaQuickSort);
        break;
      case "javaSelectionSort":
        setProg(Codes.javaSelectionSort);
        break;
      case "pythonBubbleSort":
        setProg(Codes.pythonBubbleSort);
        break;
      case "pythonInsertionSort":
        setProg(Codes.pythonInsertionSort);
        break;
      case "pythonMergeSort":
        setProg(Codes.pythonMergeSort);
        break;
      case "pythonQuickSort":
        setProg(Codes.pythonQuickSort);
        break;
      case "pythonSelectionSort":
        setProg(Codes.pythonSelectionSort);
        break;
      default:
        break;
    }
  };

  const handleProg1 = (e) => {
    setProg1(e.target.value);

    let programName = lang1 + e.target.value;
    console.log(programName);

    handleProgram(programName, setProgram1);
  };

  const handleProg2 = (e) => {
    setProg2(e.target.value);

    let programName = lang2 + e.target.value;
    console.log(programName);

    let codes = new Codes();
    // setProgram2(codes.programName);
  };



  return (
    <div className="App">
      <Container fluid>
        <Row>
          <Col md={12} style={{ height: "10vh" }}>
            <Card className="headerCard">
              <Card.Body>
                <div className="bg-blue-800">
                  <h1>Prototype for Type 4 Clone Detection using TestCases</h1>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col md={2} style={{ height: "100vh" }}>
            <Card className="leftCard">
              <Card.Body>
                <Stack gap={5}>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleLang1}
                  >
                    <option>Language 1</option>
                    <option value="java"> Java</option>
                    <option value="cpp">C++</option>
                    <option value="python">Python</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleLang2}
                  >
                    <option>Language 2</option>
                    <option value="java">Java</option>
                    <option value="cpp">C++</option>
                    <option value="python">Python</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleProg1}
                  >
                    <option>Program 1</option>
                    <option value="MergeSort">Merge Sort</option>
                    <option value="SelectionSort">Selection Sort</option>
                    <option value="QuickSort">Quick Sort</option>
                    <option value="InsertionSort">Insertion Sort</option>
                    <option value="BubbleSort">Bubble Sort</option>
                  </Form.Select>
                  <Form.Select
                    aria-label="Default select example"
                    className="item"
                    onChange={handleProg2}
                  >
                    <option>Program 2</option>
                    <option value="MergeSort">Merge Sort</option>
                    <option value="SelectionSort">Selection Sort</option>
                    <option value="QuickSort">Quick Sort</option>
                    <option value="InsertionSort">Insertion Sort</option>
                    <option value="BubbleSort">Bubble Sort</option>
                  </Form.Select>
                  <Button variant="dark" className="button" onClick={findMatch}>
                    Match
                  </Button>
                </Stack>
              </Card.Body>
            </Card>
          </Col>
          <Col md={10}>
            <Row md={9}>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>{program1}</Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="rightCard">
                  <Card.Body>{program2}</Card.Body>
                </Card>
              </Col>
            </Row>
            <Row md={3}>
              <Col md={12}>
                <Card className="bottomCard">
                  <Card.Body>{/* <h1>Hellow World</h1> */}</Card.Body>
                </Card>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
